mapper.select:
select

from items_spec
where 1 = 1

  and item_id = #{itemId,jdbcType=VARCHAR}


  and name = #{name,jdbcType=VARCHAR}


  and stock = #{stock,jdbcType=INTEGER}


  and discounts = #{discounts,jdbcType=DECIMAL}


  and price_discount = #{priceDiscount,jdbcType=INTEGER}


  and price_normal = #{priceNormal,jdbcType=INTEGER}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}

mapper.insert:

  useGeneratedKeys

insert into items_spec (id, item_id, name, 
  stock, discounts, price_discount, 
  price_normal, create_time, update_time
  )
values (#{id,jdbcType=VARCHAR}, #{itemId,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{stock,jdbcType=INTEGER}, #{discounts,jdbcType=DECIMAL}, #{priceDiscount,jdbcType=INTEGER}, 
  #{priceNormal,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
